def draw_square(id, x,y,s,c, parent):

    style = {   'stroke'        :  "none",
                'stroke-width'  :  "0",
                'fill'          : c
            }
                
    attribs = {
        'style'     :simplestyle.formatStyle(style),
        'height'    : str(s*1.01 ),
        'width'     : str(s*1.01 ),
        'x'         : str(x*s ),
        'y'         : str((7-y)*s ),
          'id' : id
            }
    circ = inkex.etree.SubElement(parent, inkex.addNS('rect','svg'), attribs )
    
def draw_border(s,parent):

    style = {   'stroke'        :  "black",
                'stroke-width'  :  str(0.06*s),
                'fill'          : "none"
            }
                
    attribs = {
        'style'     :simplestyle.formatStyle(style),
        'height'    : str(s*8 ),
        'width'     : str(s*8 ),
        'x'         : '0',
        'y'         : '0',
            }
    circ = inkex.etree.SubElement(parent, inkex.addNS('rect','svg'), attribs )

def draw_piece(x,y,s,c,p,mono,flip,parent):
  if flip:
    x=7-x
    y=7-y
  grp_attribs = {'id':'piece'+str(y*10+x)
                    }
  grp = inkex.etree.SubElement(parent, 'g', grp_attribs)
  if mono and (c==WHITE and (x+y)%2!=0 or c==BLACK and (x+y)%2==0) or not mono and c==WHITE :
    style = {   'stroke'        :  "none",
                'stroke-width'  :  str(0.06*s),
                'fill'          : "white"  if c==WHITE else "black"
            }
    attribs = {
        'style'     :simplestyle.formatStyle(style),
          'd': pics_white[p],
          'transform': 'translate('+str(x*s)+','+str((7-y)*s)+') scale('+str(s/129.0)+')',
            
            }
  
    circ=inkex.etree.SubElement(grp,'path',attribs)
    style = {   'stroke'        :  "none",
                'stroke-width'  :  str(0.06*s),
                'fill'          : "black"  if c==WHITE else "white"
            }
    attribs = {
        'style'     :simplestyle.formatStyle(style),
          'd': pics_outline[p],
          'transform': 'translate('+str(x*s)+','+str((7-y)*s)+') scale('+str(s/129.0)+')',
            
            }
    circ=inkex.etree.SubElement(grp,'path',attribs)
  else:
    style = {   'stroke'        :  "none",
                'stroke-width'  :  str(0.06*s),
                'fill'          : "white" if c==WHITE else "black"
            }
    attribs = {
        'style'     :simplestyle.formatStyle(style),
          'd': pics_black[p],
          'transform': 'translate('+str(x*s)+','+str((7-y)*s)+') scale('+str(s/129.0)+')',
            
            }
  
    circ=inkex.etree.SubElement(grp,'path',attribs)


def fen2board(fen):
  
  ws=string.split(fen,' ')
  b=[[(-1,-1) for i in range(8)] for j in range(8) ]
  y=7;
  x=0;
  for c in ws[0]:
    if (c==' '):
      break
    elif (c>='0' and c<='8'):
      x+=ord(c)-ord('0')
    elif (c=='/'):
      y-=1
      x=0
    elif (x>=0 and x<8):
      if c=='K':
        p=KING
        t=WHITE
      elif c=='Q':
        p=QUEEN
        t=WHITE
      elif c=='B':
        p=BISHOP
        t=WHITE
      elif c=='N':
        p=KNIGHT
        t=WHITE
      elif c=='R':
        p=ROOK
        t=WHITE
      elif c=='P':
        p=PAWN
        t=WHITE
      elif c=='k':
        p=KING
        t=BLACK
      elif c=='q':
        p=QUEEN
        t=BLACK
      elif c=='b':
        p=BISHOP
        t=BLACK
      elif c=='n':
        p=KNIGHT
        t=BLACK
      elif c=='r':
        p=ROOK
        t=BLACK
      elif c=='p':
        p=PAWN
        t=BLACK
      else:
        p=-1
        t=-1
      b[y][x]=(p,t)
      x+=1
    if (y<0 or y>=8):
      break
  return b

def render_chessboard(fen, piecesize, ls,ds,mono,flip,parent):
  
  grp_attribs = {'id':'chessboard'
                    }
  grp = inkex.etree.SubElement(parent, 'g', grp_attribs)
  for y in range(0,8):
    for x in range(0,8):
      if mono:
        c="white" if (x+y)%2==1 else "black"
      else:
        c='#'+ls if (x+y)%2==1 else '#'+ds
      draw_square(chr(97+x)+chr(49+y),x,y,piecesize,c,grp)
  draw_border(piecesize,grp)
  
  grp_attribs = {'id':'pieces'
                    }
  grp = inkex.etree.SubElement(parent, 'g', grp_attribs)
  b=fen2board(fen)
  for y in range(0,8):
    for x in range(0,8):
      (p,t)=b[y][x]
      if p!=-1 and t!=-1:
        draw_piece(x,y,piecesize,t,p,mono,flip,grp)
  

class InsertChessboard(inkex.Effect):
   

  def __init__(self):
    inkex.Effect.__init__(self)
    self.OptionParser.add_option("-p", "--piecesize",
            action="store", type="int",
            dest="piecesize", default=64,
            help="Piece size")
    self.OptionParser.add_option("-f", "--fen",
            action="store", type="string",
            dest="fen", default="rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR w KQkq - 0 1",
            help="FEN")
            
    self.OptionParser.add_option("-l", "--lightsquare",
            action="store", type="string",
            dest="lightsquare", default="ffffff",
            help="Color of light square")
    self.OptionParser.add_option("-d", "--darksquare",
            action="store", type="string",
            dest="darksquare", default="000000",
            help="Color of dark square")
    self.OptionParser.add_option("-m", "--mono",
            action="store", type="inkbool",
            dest="mono", default=True,
            help="monochrome")
    self.OptionParser.add_option("-r", "--flip",
            action="store", type="inkbool",
            dest="flip", default=True,
            help="flipboard")
           


  def effect(self):
    so = self.options
    size=so.piecesize*self.unittouu('1mm')
    x, y = self.view_center
    x-=size*4
    y-=size*4
    grp_transform = 'translate' + str( (x,y) )
    grp_name = 'ChessBoard'
    grp_attribs = {inkex.addNS('label','inkscape'):grp_name,
                   'transform':grp_transform }
    grp = inkex.etree.SubElement(self.current_layer, 'g', grp_attribs)#the group to put everything in

    render_chessboard(so.fen, size, so.lightsquare, so.darksquare,so.mono,so.flip, grp )    # generate the SVG elements

if __name__ == '__main__':
        e = InsertChessboard()
        e.affect()

