def draw_gridline(id, x1,y1,x2,y2,s, c, parent):

    style = {   'stroke'        :  c,
                'stroke-width'  :  str(s/32.0),
                'fill'          : "none"
            }
                
    attribs = {
        'style'     :simplestyle.formatStyle(style),
        'x1'         : str(x1*s+s/2 ),
        'y1'         : str((9-y1)*s+s/2 ),
        'x2'         : str(x2*s+s/2 ),
        'y2'         : str((9-y2)*s+s/2 ),
          'id' : id
            }
    circ = inkex.etree.SubElement(parent, inkex.addNS('line','svg'), attribs )

