def draw_border(s,c, parent):

    style = {   'stroke'        :  c,
                'stroke-width'  :  str(s/12.0),
                'fill'          : "none"
            }
                
    attribs = {
        'style'     :simplestyle.formatStyle(style),
        'height'    : str(s*9+s/6.0 ),
        'width'     : str(s*8+s/6.0 ),
        'x'         : str(-s/12.0+s/2),
        'y'         : str(-s/12.0+s/2),
            }
    circ = inkex.etree.SubElement(parent, inkex.addNS('rect','svg'), attribs )

