def fen2board(fen):
  
  ws=string.split(fen,' ')
  b=[[(-1,-1) for i in range(9)] for j in range(10) ]
  y=9;
  x=0;
  for c in ws[0]:
    if (c==' '):
      break
    elif (c>='0' and c<='9'):
      x+=ord(c)-ord('0')
    elif (c=='/'):
      y-=1
      x=0
    elif (x>=0 and x<9):
      if c=='K' or c=='G':
        p=GENERAL
        t=WHITE
      elif c=='A':
        p=ADVISOR
        t=WHITE
      elif c=='E' or c=='B':
        p=ELEPHANT
        t=WHITE
      elif c=='H' or c=='N':
        p=HORSE
        t=WHITE
      elif c=='R':
        p=CHARIOT
        t=WHITE
      elif c=='C':
        p=CANNON
        t=WHITE
      elif c=='P' or c=='S':
        p=SOLDIER
        t=WHITE
      elif c=='k' or c=='g':
        p=GENERAL
        t=BLACK
      elif c=='a':
        p=ADVISOR
        t=BLACK
      elif c=='e' or c=='b':
        p=ELEPHANT
        t=BLACK
      elif c=='h' or c=='n':
        p=HORSE
        t=BLACK
      elif c=='r':
        p=CHARIOT
        t=BLACK
      elif c=='c':
        p=CANNON
        t=BLACK
      elif c=='p' or c=='s':
        p=SOLDIER
        t=BLACK
      else:
        p=-1
        t=-1
      b[y][x]=(p,t)
      x+=1
    if (y<0 or y>9):
      break
  return b

def render_chessboard(fen, piecesize, gc, mono,flip,parent):
  
  grp_attribs = {'id':'chessboard'
                    }
  grp = inkex.etree.SubElement(parent, 'g', grp_attribs)
  if mono:
    bc="white"
    c="black"
  else:
    bc="#"+gc
    c="#400000"
  style = {   'stroke'        :   "none" if mono else "black",
               'stroke-width' : str(piecesize/24.0),
                'fill'          : bc
            }
  attribs = {
      'style'     :simplestyle.formatStyle(style),
        'x': str(0),
        'y': str(0),
        'width': str(piecesize*9),
        'height': str(piecesize*10),
        'id':"board"
        }
  circ=inkex.etree.SubElement(grp,'rect',attribs)
  
  for y in range(0,10):
      x1=0
      x2=8
      draw_gridline(str(y+1),x1,y,x2,y,piecesize,c,grp)
  draw_gridline(chr(0+97),0,0,0,9,piecesize,c,grp)
  for x in range(1,8):
      y1=0
      y2=4
      draw_gridline(chr(x+97)+'n',x,y1,x,y2,piecesize,c,grp)
      y1=5
      y2=9
      draw_gridline(chr(x+97)+'f',x,y1,x,y2,piecesize,c,grp)
  draw_gridline(chr(8+97),8,0,8,9,piecesize,c,grp)
  draw_gridline(chr(3+97)+'c',3,0,5,2,piecesize,c,grp)
  draw_gridline(chr(5+97)+'c',5,0,3,2,piecesize,c,grp)
  draw_gridline(chr(3+97)+'d',3,9,5,7,piecesize,c,grp)
  draw_gridline(chr(5+97)+'d',5,9,3,7,piecesize,c,grp)
  
  draw_point("p%c%d"%(chr(1+97),2+1),1,2,piecesize,c,grp);
  draw_point("p%c%d"%(chr(7+97),2+1),7,2,piecesize,c,grp);
  draw_point("p%c%d"%(chr(1+97),7+1),1,7,piecesize,c,grp);
  draw_point("p%c%d"%(chr(7+97),7+1),7,7,piecesize,c,grp);

  draw_point("p%c%d"%(chr(0+97),3+1),0,3,piecesize,c,grp);
  draw_point("p%c%d"%(chr(2+97),3+1),2,3,piecesize,c,grp);
  draw_point("p%c%d"%(chr(4+97),3+1),4,3,piecesize,c,grp);
  draw_point("p%c%d"%(chr(6+97),3+1),6,3,piecesize,c,grp);
  draw_point("p%c%d"%(chr(8+97),3+1),8,3,piecesize,c,grp);
  draw_point("p%c%d"%(chr(0+97),6+1),0,6,piecesize,c,grp);
  draw_point("p%c%d"%(chr(2+97),6+1),2,6,piecesize,c,grp);
  draw_point("p%c%d"%(chr(4+97),6+1),4,6,piecesize,c,grp);
  draw_point("p%c%d"%(chr(6+97),6+1),6,6,piecesize,c,grp);
  draw_point("p%c%d"%(chr(8+97),6+1),8,6,piecesize,c,grp);
 
  draw_hz('chuhehanjie',piecesize,c,flip,grp)
 
  draw_border(piecesize,c,grp)
  
  grp_attribs = {'id':'pieces'
                    }
  grp = inkex.etree.SubElement(parent, 'g', grp_attribs)
  b=fen2board(fen)
  for y in range(0,10):
    for x in range(0,9):
      (p,t)=b[y][x]
      if p!=-1 and t!=-1:
        draw_piece(x,y,piecesize,t,p,mono,flip,grp)

class InsertChessboard(inkex.Effect):
   

  def __init__(self):
    inkex.Effect.__init__(self)
    self.OptionParser.add_option("-p", "--piecesize",
            action="store", type="int",
            dest="piecesize", default=64,
            help="Piece size")
    self.OptionParser.add_option("-f", "--fen",
            action="store", type="string",
            dest="fen", default="rheakaehr/9/1c5c1/p1p1p1p1p/9/9/P1P1P1P1P/1C5C1/9/RHEAKAEHR w 0 1",
            help="FEN")
            
    self.OptionParser.add_option("-b", "--boardcolor",
            action="store", type="string",
            dest="boardcolor", default="ffffc0",
            help="Color of board")
    self.OptionParser.add_option("-m", "--mono",
            action="store", type="inkbool",
            dest="mono", default=True,
            help="monochrome")
    self.OptionParser.add_option("-r", "--flip",
            action="store", type="inkbool",
            dest="flip", default=True,
            help="flipboard")
           


  def effect(self):
    so = self.options
    x, y = self.view_center
    size=so.piecesize*self.unittouu('1mm')
    x-=size*4.5
    y-=size*5
    grp_transform = 'translate' + str( (x,y) )
    grp_name = 'Chinese ChessBoard'
    grp_attribs = {inkex.addNS('label','inkscape'):grp_name,
                   'transform':grp_transform }
    grp = inkex.etree.SubElement(self.current_layer, 'g', grp_attribs)#the group to put everything in

    render_chessboard(so.fen, size, so.boardcolor, so.mono,so.flip, grp )    # generate the SVG elements

if __name__ == '__main__':
        e = InsertChessboard()
